// AttendancePage.js
import React, { useState } from "react";
import { TextField, Button, Container, Grid } from "@mui/material";

const Add = () => {
  const [storeName, setStoreName] = useState("");
  const [email, setEmail] = useState("");
  const [salary, setSalary] = useState("");

  const handleMarkPresent = () => {
    // Implement the logic to mark the employee present (update backend or state)
    console.log("Employee marked present!");
  };

  return (
    <Container>
      <Grid container spacing={2}>
        <Grid item xs={12}>
          <TextField
            fullWidth
            label="Store Name"
            variant="outlined"
            value={storeName}
            onChange={(e) => setStoreName(e.target.value)}
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            fullWidth
            label="Email Address"
            variant="outlined"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            fullWidth
            label="Salary"
            variant="outlined"
            value={salary}
            onChange={(e) => setSalary(e.target.value)}
          />
        </Grid>
        <Grid item xs={12}>
          <Button
            variant="contained"
            color="primary"
            onClick={handleMarkPresent}
          >
            Mark Present
          </Button>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Add;
