import React, { useState, useEffect } from "react";
import { useParams, Navigate, useNavigate } from "react-router-dom";

const Update = () => {
  const [name, setName] = useState("");
  const [rollno, setRollno] = useState("");
  const [section, setSection] = useState("");
  const [branch, setBranch] = useState("");
  const [mobile, setMoblileno] = useState("");
  const [fathername, setFathername] = useState("");
  const [address, setAddress] = useState("");
  const [collagename, setCollagename] = useState("");
  const params = useParams();
  const navigate = useNavigate();
  useEffect(() => {
    getDetails();
  }, []);

  const getDetails = async () => {
    let result = await fetch(`http://localhost:5000/detail/${params.id}`);
    result = await result.json();
    console.log({ result });
    setName(result.name);
    setRollno(result.rollno);
    setSection(result.section);
    setBranch(result.branch);
    setMoblileno(result.mobile);
    setFathername(result.fathername);
    setAddress(result.address);
    setCollagename(result.collagename);
  };

  // const UpdateDetails = async () => {
  //   console.warn(
  //     name,
  //     rollno,
  //     section,
  //     branch,
  //     mobile,
  //     fathername,
  //     address,
  //     collagename
  //   );
  // };

  const UpdateDetails = async () => {
    try {
      const response = await fetch(
        `http://localhost:5000/update/${params.id}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            name,
            rollno,
            section,
            branch,
            mobile,
            fathername,
            address,
            collagename,
          }),
        }
      );

      if (!response.ok) {
        throw new Error("Failed to update details");
      }

      // Optionally, you can handle the successful update here, such as showing a success message or navigating to a different page.
      console.log("Details updated successfully");
      // Example: Navigate to a different page after successful update
      // navigate('/success'); // You need to import `navigate` from 'react-router-dom'
    } catch (error) {
      console.error("Error updating details:", error);
      // Optionally, you can handle the error here, such as showing an error message to the user.
    }
  };
  return (
    <div>
      <div className="add">
        <h5>Update Product</h5>
        <input
          type="text"
          value={name}
          className="inputBox1"
          placeholder="Enter Name"
          onChange={(e) => setName(e.target.value)}
        />
        <input
          type="text"
          className="inputBox1"
          value={rollno}
          placeholder="Enter Roll.no"
          onChange={(e) => setRollno(e.target.value)}
        />

        <input
          type="text"
          value={section}
          className="inputBox1"
          placeholder="Enter Section"
          onChange={(e) => setSection(e.target.value)}
        />
        <input
          type="text"
          className="inputBox1"
          value={branch}
          placeholder="Enter Branch"
          onChange={(e) => setBranch(e.target.value)}
        />

        <input
          type="text"
          value={mobile}
          className="inputBox1"
          placeholder="Mobile.No"
          onChange={(e) => setMoblileno(e.target.value)}
        />

        <input
          type="text"
          className="inputBox1"
          value={fathername}
          placeholder="Father's Name"
          onChange={(e) => setFathername(e.target.value)}
        />

        <input
          type="text"
          className="inputBox1"
          value={address}
          placeholder="Address"
          onChange={(e) => setAddress(e.target.value)}
        />

        <input
          type="text"
          value={collagename}
          className="inputBox1"
          placeholder="Collage Name"
          onChange={(e) => setCollagename(e.target.value)}
        />
        <button className="button1" onClick={UpdateDetails}>
          Update Details
        </button>
      </div>
    </div>
  );
};
export default Update;
