import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import Update from "./Update";
const Home = () => {
  const [product, setProduct] = useState([]);
  useEffect(() => {
    getProduct();
  }, []);

  const deleteDetals = async (id) => {
    let result = await fetch(`http://localhost:5000/details/${id}`, {
      method: "Delete",
    });
    result = await result.json();
    if (result) {
      getProduct();
      alert("record is Deleted");
    }
  };

  const getProduct = async () => {
    let result = await fetch("http://localhost:5000/details");
    result = await result.json();
    setProduct(result);
  };
  const searchHandel = async () => {
    //console.warn(.target.value);
  };
  // console.warn("list", list);

  // const fun = (id) => {
  //   <Link to={`/update/:id` + id}>
  //     <Update />
  //   </Link>;
  // };
  return (
    <div className="detailsList">
      <h3>STUDENT-LIST</h3>
      <input
        type="text"
        className="search"
        placeholder="search"
        onChange={searchHandel}
      />
      <ul className="">
        <li>S.No</li>
        <li>Name</li>
        <li>Roll.No</li>
        <li>Section</li>
        <li>Branch</li>
        <li>Mobile.No</li>
        <li>Father's-Name</li>
        <li>Address</li>
        <li>Collage Name</li>
        {/* <li>Delete</li>
        <li>Update</li> */}
      </ul>

      {product.map((item, index) => (
        <ul key={item._id}>
          <li>{index + 1}</li>
          <li>{item.name}</li>
          <li>{item.rollno}</li>
          <li>{item.section}</li>
          <li>{item.branch}</li>
          <li>{item.mobile}</li>
          <li>{item.fathername}</li>
          <li>{item.address}</li>
          <li>{item.collagename}</li>
          <li>
            <button className="button1" onClick={() => deleteDetals(item._id)}>
              Delete
            </button>
          </li>
          <li>
            <button className="button1">
              <Link to={`/update/` + item._id}>Update</Link>
            </button>
          </li>
        </ul>
      ))}

      <hr></hr>
    </div>
  );
};
export default Home;

