import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";

const Update = () => {
  const [name, setName] = useState("");
  const [rollno, setRollno] = useState("");
  const [section, setSection] = useState("");
  const [branch, setBranch] = useState("");
  const [mobile, setMoblileno] = useState("");
  const [fathername, setFathername] = useState("");
  const [address, setAddress] = useState("");
  const [collagename, setCollagename] = useState("");
  const params = useParams();

  useEffect(() => {
    getDetails();
  }, []);

  const getDetails = async () => {
    console.warm(params);
    let result = await fetch(`http://localhost:5000/detail/${params.id}`);
    result = await result.json();
    //console.warn(result);
    setName(result.name);
  };
  const navigate = useNavigate();
  // useEffect(() => {
  //   const auth = localStorage.getItem("details");
  //   if (auth) {
  //     navigate("/home");
  //   }
  // }, []);
  const UpdateDetails = async () => {
    console.warn(
      name,
      rollno,
      section,
      branch,
      mobile,
      fathername,
      address,
      collagename
    );
  };
  return (
    <div>
      <div className="add">
        <h5>Update Product</h5>
        <input
          type="text"
          value={name}
          className="inputBox1"
          placeholder="Enter Name"
          onChange={(e) => setName(e.target.value)}
        />
        <input
          type="text"
          className="inputBox1"
          value={rollno}
          placeholder="Enter Roll.no"
          onChange={(e) => setRollno(e.target.value)}
        />

        <input
          type="text"
          value={section}
          className="inputBox1"
          placeholder="Enter Section"
          onChange={(e) => setSection(e.target.value)}
        />
        <input
          type="text"
          className="inputBox1"
          value={branch}
          placeholder="Enter Branch"
          onChange={(e) => setBranch(e.target.value)}
        />

        <input
          type="text"
          value={mobile}
          className="inputBox1"
          placeholder="Mobile.No"
          onChange={(e) => setMoblileno(e.target.value)}
        />

        <input
          type="text"
          className="inputBox1"
          value={fathername}
          placeholder="Father's Name"
          onChange={(e) => setFathername(e.target.value)}
        />

        <input
          type="text"
          className="inputBox1"
          value={address}
          placeholder="Address"
          onChange={(e) => setAddress(e.target.value)}
        />

        <input
          type="text"
          value={collagename}
          className="inputBox1"
          placeholder="Collage Name"
          onChange={(e) => setCollagename(e.target.value)}
        />

        <button className="button1" onClick={UpdateDetails}>
          Update Details
        </button>
      </div>
    </div>
  );
};
export default Update;
