// AttendancePage.js
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { TextField, Button, Container, Grid } from "@mui/material";

const Add = () => {
  const [name, setName] = useState("");
  const [rollno, setRollno] = useState("");
  const [section, setSection] = useState("");
  const [branch, setBranch] = useState("");
  const [mobile, setMoblileno] = useState("");
  const [fathername, setFathername] = useState("");
  const [address, setAddress] = useState("");
  const [collagename, setCollagename] = useState("");

  const navigate = useNavigate();
  // useEffect(() => {
  //   const auth = localStorage.getItem("details");
  //   if (auth) {
  //     navigate("/home");
  //   }
  // }, []);
  const loginhandel = async () => {
    const userid = JSON.parse(localStorage.getItem("user"))._id;
    const result = await fetch("http://localhost:5000/adddetail", {
      method: "post",
      body: JSON.stringify({
        name,
        rollno,
        section,
        branch,
        mobile,
        fathername,
        address,
        collagename,
      }),
      headers: {
        "Content-Type": "application/json",
      },
    });
    console.warn({
      name,
      rollno,
      section,
      branch,
      mobile,
      fathername,
      address,
      collagename,
    });
    result = await result.json();
    console.warn(result);
    if (result.name) {
      localStorage.setItem("user", JSON.stringify(result));
      navigate("/home");
    } else {
      alert("Please Enter Correct details");
    }
  };
  return (
    <div>
      <div className="add">
        <input
          type="text"
          value={name}
          className="inputBox1"
          placeholder="Enter Name"
          onChange={(e) => setName(e.target.value)}
        ></input>
        <input
          type="text"
          className="inputBox1"
          value={rollno}
          placeholder="Enter Roll.no"
          onChange={(e) => setRollno(e.target.value)}
        ></input>
        <input
          type="text"
          value={section}
          className="inputBox1"
          placeholder="Enter Section"
          onChange={(e) => setSection(e.target.value)}
        ></input>
        <input
          type="text"
          className="inputBox1"
          value={branch}
          placeholder="Enter Branch"
          onChange={(e) => setBranch(e.target.value)}
        ></input>
        <input
          type="text"
          value={mobile}
          className="inputBox1"
          placeholder="Mobile.No"
          onChange={(e) => setMoblileno(e.target.value)}
        ></input>
        <input
          type="text"
          className="inputBox1"
          value={fathername}
          placeholder="Father's Name"
          onChange={(e) => setFathername(e.target.value)}
        ></input>
        <input
          type="text"
          className="inputBox1"
          value={address}
          placeholder="Address"
          onChange={(e) => setAddress(e.target.value)}
        ></input>
        <input
          type="text"
          value={collagename}
          className="inputBox1"
          placeholder="Collage Name"
          onChange={(e) => setCollagename(e.target.value)}
        ></input>
        <button className="button1" onClick={loginhandel}>
          Add Details
        </button>
      </div>
    </div>
  );
};

export default Add;
