import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const Update = () => {
  const [name, setName] = useState("");
  const [rollno, setRollno] = useState("");
  const [section, setSection] = useState("");
  const [branch, setBranch] = useState("");
  const [mobile, setMoblileno] = useState("");
  const [fathername, setFathername] = useState("");
  const [address, setAddress] = useState("");
  const [collagename, setCollagename] = useState("");
  const newname = useParams();

  useEffect(() => {
    //getDetails();
    UpdateDetails();
  }, []);

  // const getDetails = async () => {
  //   let result = await fetch(`http://localhost:5000/detail/${newname.id}`);
  //   result = await result.json();
  //   console.log({ result });
  //   setName(result.name);
  //   setRollno(result.rollno);
  //   setSection(result.section);
  //   setBranch(result.branch);
  //   setMoblileno(result.mobile);
  //   setFathername(result.fathername);
  //   setAddress(result.address);
  //   setCollagename(result.collagename);
  // };

  const UpdateDetails = async () => {
    console.warn(
      name,
      rollno,
      section,
      branch,
      mobile,
      fathername,
      address,
      collagename
    );
    let result = await fetch(`http://localhost:5000/detail/${newname.id}`);
    result = await result.json();
    console.log({ result });
    setName(result.name);
    setRollno(result.rollno);
    setSection(result.section);
    setBranch(result.branch);
    setMoblileno(result.mobile);
    setFathername(result.fathername);
    setAddress(result.address);
    setCollagename(result.collagename);
  };
  return (
    <div>
      <div className="add">
        <h5>Update Product</h5>
        <input
          type="text"
          value={name}
          className="inputBox1"
          placeholder="Enter Name"
          onChange={(e) => setName(e.target.value)}
        />
        <input
          type="text"
          className="inputBox1"
          value={rollno}
          placeholder="Enter Roll.no"
          onChange={(e) => setRollno(e.target.value)}
        />

        <input
          type="text"
          value={section}
          className="inputBox1"
          placeholder="Enter Section"
          onChange={(e) => setSection(e.target.value)}
        />
        <input
          type="text"
          className="inputBox1"
          value={branch}
          placeholder="Enter Branch"
          onChange={(e) => setBranch(e.target.value)}
        />

        <input
          type="text"
          value={mobile}
          className="inputBox1"
          placeholder="Mobile.No"
          onChange={(e) => setMoblileno(e.target.value)}
        />

        <input
          type="text"
          className="inputBox1"
          value={fathername}
          placeholder="Father's Name"
          onChange={(e) => setFathername(e.target.value)}
        />

        <input
          type="text"
          className="inputBox1"
          value={address}
          placeholder="Address"
          onChange={(e) => setAddress(e.target.value)}
        />

        <input
          type="text"
          value={collagename}
          className="inputBox1"
          placeholder="Collage Name"
          onChange={(e) => setCollagename(e.target.value)}
        />
        <button className="button1" onClick={UpdateDetails}>
          Update Details
        </button>
      </div>
    </div>
  );
};
export default Update;
