import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import Update from "./Update";
const Home = () => {
  //const [list, setList] = useState([]);
  const [product, setProduct] = useState([]);
  useEffect(() => {
    //getLists();
    getProduct();
  }, []);

  const deleteDetals = async (id) => {
    let result = await fetch(`http://localhost:5000/details/${id}`, {
      method: "Delete",
    });
    result = await result.json();
    if (result) {
      getProduct();
      alert("record is Deleted");
    }
  };

  const getProduct = async () => {
    let result = await fetch("http://localhost:5000/details");
    result = await result.json();
    setProduct(result);
  };
  // console.warn("list", list);

  return (
    <div className="detailsList">
      <h3>STUDENT-LIST</h3>
      <ul className="">
        <li>S.No</li>
        <li>Name</li>
        <li>Roll.No</li>
        <li>Section</li>
        <li>Branch</li>
        <li>Mobile.No</li>
        <li>Father's-Name</li>
        <li>Address</li>
        <li>Collage Name</li>
      </ul>

      {product.map((item, index) => (
        <ul key={item._id}>
          <li>{index + 1}</li>
          <li>{item.name}</li>
          <li>{item.rollno}</li>
          <li>{item.section}</li>
          <li>{item.branch}</li>
          <li>{item.mobile}</li>
          <li>{item.fathername}</li>
          <li>{item.address}</li>
          <li>{item.collagename}</li>
          <li>
            <button onClick={() => deleteDetals(item._id)}>Delete</button>

            <button className="button">
              <Link to={`/update/:id` + item._id}>
                <Update />
              </Link>
            </button>
          </li>
        </ul>
      ))}

      <hr></hr>
    </div>
  );
};
export default Home;

